DROP TABLE IF EXISTS PRODUCT;
CREATE TABLE PRODUCT(
	productid int auto_increment primary key,
    name varchar(50),
    color varchar(20),
    overview varchar(100),
    productsize varchar(100),
    price double,
    stock int,
    description varchar(500)
);

